import { faTrashCan } from '@fortawesome/free-solid-svg-icons';
import { useState } from 'react'

function Task(props){
    function deleteHandler(){
        console.log(`Clicked ${props.details}`);
    }

    return (
        <div className="card">
            <div>
                <input type="checkbox"></input>{props.details}
                <button className='btn' onClick={deleteHandler}><FontAwesomeIcon icon={faTrashCan}/>{props.buttonText}</button>
            </div>
        </div>
    );
}

export default Task;
