import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlusSquare } from '@fortawesome/free-solid-svg-icons';
import { useState, useEffect} from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css'; 

function TaskAdder(){
    const [text, setText] = useState("");
    const [response, setResponse] = useState(null);

    useEffect(() => {
      fetch("/tasks")
        .then((res) => res.json())
        .then((res) => setResponse(res.message));
    }, []);

    function addTaskHandler(){
        if(isEmptyOrSpaces(text)) {
            toast.info("Please input task details");
            return;
        }

        fetch('http://localhost:8000/tasks/add',
            {   
                headers: {
                    'Content-Type': 'application/json'
                },
                method: 'POST',
                body: JSON.stringify({"details": text})
            }
        )
        .then(res => {
            if(!res.ok){
                toast.error("Error adding task");
                setResponse("Server Error. Failed to add task!");
            }
            toast.success("Task added successfully");
        })
        .catch(e => {
            toast.error("Error adding task");
            setResponse(e.message);
        });
        setText("");
    }

    function onChangeHandler(evt){
        setText(evt.target.value);
    }

    function isEmptyOrSpaces(str){
        return str === null || str.match(/^ *$/) !== null;
    }

    return (
        <div>
            <input type="text" value={text} onChange={evt => onChangeHandler(evt)}></input>
            <button className='btn' onClick={addTaskHandler}><FontAwesomeIcon icon={faPlusSquare}/></button>
            <p>{!response?"Loading":response}</p>
            <ToastContainer 
                position="top-center"
                autoClose={5000}
                hideProgressBar={false}
                newestOnTop={false}
                closeOnClick
                rtl={false}
                pauseOnFocusLoss
                draggable
                pauseOnHover
            />
        </div>
    );
}

export default TaskAdder;
