import './App.css';
import { useState, useEffect } from  'react';
//import TaskList from './components/TaskList'
import TaskAdder from './components/TaskAdder';

function App() {
  const [taskList, setTaskList] = useState(null);

  useEffect(() => {
    const tasks = fetch("http://localhost:8000/tasks")
      .then((res) => res.json)
      .then((data) => {
        console.log(JSON.stringify(data));
        setTaskList(JSON.stringify(data));
      })
      .catch((err)=>{
        console.log("Error fetching");
      });
    setTaskList(tasks);
  }, []);

  return (
    <div className="App">
      <h1>Add Task</h1>
      <TaskAdder />
      <h1>Tasks List</h1>
      <p>{taskList}</p>
    </div>
  );
}

export default App;
