import './App.css';
import { useState, useEffect } from  'react';
//import TaskList from './components/TaskList'
import TaskAdder from './components/TaskAdder';

function App() {
  const [taskList, setTaskList] = useState(null);

  useEffect(() => {
    function fetchTasks(){
      fetch("http://localhost:8000/tasks",{
        method: 'GET'
      })
      .then((res) => {
        console.log(res.json);
      })
      .then((data) => {
        console.log(data.json);
        setTaskList(JSON.stringify(data.json));
      })
      .catch((err)=>{
        console.log(err);
      });
    }

    fetchTasks();
  }, []);

  return (
    <div className="App">
      <h1>Add Task</h1>
      <TaskAdder />
      <h1>Tasks List</h1>
      <p>{taskList}</p>
    </div>
  );
}

export default App;
