import React, { Component } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrashCan } from '@fortawesome/free-solid-svg-icons'
import './App.css';

function App() {
  return (
    <div className="App">
      <h1>Tasks</h1>
      <Task
        details="sample task"
        buttonText=""
      />
    </div>
  );
}

function Task(props){
  return (
    <div className="card">
      <div>
        <input type="checkbox"></input>
        <text>{props.details}</text>
        <button><FontAwesomeIcon icon={faTrashCan}/>{props.buttonText}</button>
      </div>
    </div>
  );
}
export default App;
