import './App.css';
import { useEffect, useRef } from  'react';
import TaskAdder from './components/TaskAdder';
import TaskList from './components/TaskList';

function App() {
  const taskRef = useRef([]);

  useEffect(() => {
      fetch("/tasks")
      .then((res) => res.json())
      .then((data) => {
        taskRef.current = data;
      })
      .catch((err)=>{
        console.log(err);
      });
  }, []);

  return (
    <div className="App">
      <h1>Add Task</h1>
      <TaskAdder />
      <h1>Tasks List</h1>
      {taskRef.current.length>0?<TaskList list={taskRef.current}/>:<p>No Task</p>}

    </div>
  );
}

export default App;
