import './App.css';
import { useState, useEffect } from  'react';
import TaskAdder from './components/TaskAdder';
import TaskList from './components/TaskList';

function App() {
  const [taskList, setTaskList] = useState(null);

  useEffect(() => {
    // function fetchTasks(){
      fetch("/tasks")
      .then((res) => res.json())
      .then((data) => {
        data.map((element)=>{
          console.log(element._id);
          console.log(element.details);
        });

        setTaskList(data);
      })
      .catch((err)=>{
        console.log(err);
      });
    // }

    // fetchTasks();
  }, []);

  return (
    <div className="App">
      <h1>Add Task</h1>
      <TaskAdder />
      <h1>Tasks List</h1>
      <TaskList list={taskList}/>
    </div>
  );
}

export default App;
